mapper.insert:
SELECT LAST_INSERT_ID() AS id
insert into s_user_daily_profit (
) values ( #{entity.uid}, #{entity.date}, #{entity.profit}, #{entity.currentProfit}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 0 )

mapper.select:
select count(1)
from s_user_daily_profit
where uid = #{uid}
and date = #{date}
