mapper.insert:
SELECT LAST_INSERT_ID()
insert into borrower_repayment_plan (
) values( #{entity.id}, #{entity.repaymentDate}, #{entity.borrower}, #{entity.projectCode}, #{entity.repaymentType}, #{entity.repaymentPrn}, #{entity.repaymentInterest}, #{entity.actualRepaymentPrn}, #{entity.actualRepaymentInterest}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP )

mapper.select:
