mapper.insert:
SELECT LAST_INSERT_ID()
insert into project_quota_report (
) values( #{entity.id}, #{entity.reportDate}, #{entity.projectName}, #{entity.projectCode}, #{entity.projectFinancingAmt}, #{entity.issuedAmt}, #{entity.remainIssueAmt}, #{entity.withdrewAmt}, #{entity.repaidPrn}, #{entity.unrepayPrn}, #{entity.repaidInterest}, #{entity.unrepayInterest}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP )

mapper.select:
select
count(1)
from project_quota_report
